from rest_framework import viewsetsfrom rest_framework.decorators import list_routefrom core.decorator.response import Core_connectorfrom apps.user.models import Usersfrom apps.user.serializers import UsersSerializerclass UserAPIView(viewsets.ViewSet):    @list_route(methods=['POST'])    @Core_connector(transaction=True,serializer_class=UsersSerializer,model_class=Users)    def register(self, request,*args,**kwargs):        serializer = kwargs.pop('serializer')        serializer.save()        return None